/**
 * Scenario type definitions based on Kaner's scenario testing methodology
 */

export interface Scenario {
  /** Unique identifier for the scenario */
  id: string;

  /** Human-readable name */
  name: string;

  /** The motivating story - WHY this matters */
  story: string;

  /** What the user is trying to accomplish */
  goal: string;

  /** The stakeholder who cares about this working */
  stakeholder: string;

  /** High-level steps (not low-level actions) */
  steps: string[];

  /** What success looks like (narrative, not assertions) */
  expectedBenefit: string;

  /** Optional: specific expected outcomes */
  expectedOutcomes?: string[];
}

export interface ScenarioResult {
  scenarioId: string;
  status: 'pass' | 'fail' | 'error';

  /** Steps executed */
  executedSteps: ExecutedStep[];

  /** Final evaluation from oracle */
  evaluation: OracleEvaluation;

  /** Bug report if failure detected */
  bugReport?: BugReport;

  /** Execution metadata */
  metadata: {
    startTime: Date;
    endTime: Date;
    duration: number;
    model: string;
  };
}

export interface ExecutedStep {
  /** Step description */
  description: string;

  /** Actions taken */
  actions: Action[];

  /** Screenshot after this step */
  screenshot?: string; // base64 or path

  /** Agent's reasoning */
  reasoning: string;

  /** Oracle evaluation for this step */
  evaluation: OracleEvaluation;
}

export interface Action {
  type: 'click' | 'type' | 'drag' | 'screenshot' | 'navigate';
  timestamp: Date;
  params: Record<string, unknown>;
}

export interface OracleEvaluation {
  /** Pass, investigate, or bug detected */
  status: 'pass' | 'investigate' | 'bug';

  /** Agent's reasoning about what it sees */
  reasoning: string;

  /** Confidence level (0-1) */
  confidence: number;

  /** Specific issues identified */
  issues?: string[];
}

export interface BugReport {
  /** Kaner-style motivating story */
  story: string;

  /** Steps to reproduce */
  stepsToReproduce: string[];

  /** Expected vs actual */
  expected: string;
  actual: string;

  /** Why this matters (stakeholder impact) */
  impact: string;

  /** Evidence (screenshots, logs) */
  evidence: {
    screenshots: string[];
    logs?: string[];
  };

  /** Severity assessment */
  severity: 'critical' | 'high' | 'medium' | 'low';
}

