/**
 * Computer-use abstraction layer on top of Playwright
 * Provides coordinate-based interaction (click, type, screenshot)
 * instead of semantic selectors
 */

import { Page, Browser, chromium } from 'playwright';

export interface ComputerUseTools {
  click(x: number, y: number): Promise<void>;
  type(text: string): Promise<void>;
  press(key: string): Promise<void>;
  drag(x1: number, y1: number, x2: number, y2: number): Promise<void>;
  screenshot(): Promise<string>; // Returns base64
  navigate(url: string): Promise<void>;
  getScreenSize(): Promise<{ width: number; height: number }>;
  close(): Promise<void>;
}

export class PlaywrightComputerUse implements ComputerUseTools {
  private page: Page | null = null;
  private browser: Browser | null = null;

  async initialize(options?: { headless?: boolean }): Promise<void> {
    this.browser = await chromium.launch({
      headless: options?.headless ?? true,
    });

    this.page = await this.browser.newPage({
      viewport: { width: 1280, height: 720 },
    });
  }

  async click(x: number, y: number): Promise<void> {
    if (!this.page) throw new Error('Browser not initialized');
    await this.page.mouse.click(x, y);
  }

  async type(text: string): Promise<void> {
    if (!this.page) throw new Error('Browser not initialized');
    await this.page.keyboard.type(text);
  }

  async press(key: string): Promise<void> {
    if (!this.page) throw new Error('Browser not initialized');
    await this.page.keyboard.press(key);
  }

  async drag(x1: number, y1: number, x2: number, y2: number): Promise<void> {
    if (!this.page) throw new Error('Browser not initialized');
    await this.page.mouse.move(x1, y1);
    await this.page.mouse.down();
    await this.page.mouse.move(x2, y2);
    await this.page.mouse.up();
  }

  async screenshot(): Promise<string> {
    if (!this.page) throw new Error('Browser not initialized');
    const buffer = await this.page.screenshot({ type: 'png' });
    return buffer.toString('base64');
  }

  async navigate(url: string): Promise<void> {
    if (!this.page) throw new Error('Browser not initialized');
    await this.page.goto(url, { waitUntil: 'networkidle' });
  }

  async getScreenSize(): Promise<{ width: number; height: number }> {
    if (!this.page) throw new Error('Browser not initialized');
    const viewport = this.page.viewportSize();
    if (!viewport) throw new Error('No viewport');
    return viewport;
  }

  async close(): Promise<void> {
    if (this.page) await this.page.close();
    if (this.browser) await this.browser.close();
  }
}

/**
 * Tool definitions for LangGraph agent
 * Wraps computer-use primitives in LangGraph tool format
 */

import { z } from 'zod';
import { ComputerUseTools } from './browser.js';

export function createComputerUseTools(computerUse: ComputerUseTools) {
  return {
    click: {
      name: 'click',
      description: 'Click at specific screen coordinates',
      schema: z.object({
        x: z.number().describe('X coordinate'),
        y: z.number().describe('Y coordinate'),
      }),
      func: async ({ x, y }: { x: number; y: number }) => {
        await computerUse.click(x, y);
        return `Clicked at (${x}, ${y})`;
      },
    },

    type: {
      name: 'type',
      description: 'Type text at current cursor position',
      schema: z.object({
        text: z.string().describe('Text to type'),
      }),
      func: async ({ text }: { text: string }) => {
        await computerUse.type(text);
        return `Typed: "${text}"`;
      },
    },

    press: {
      name: 'press',
      description: 'Press a keyboard key (Enter, Tab, Escape, etc.)',
      schema: z.object({
        key: z.string().describe('Key to press'),
      }),
      func: async ({ key }: { key: string }) => {
        await computerUse.press(key);
        return `Pressed: ${key}`;
      },
    },

    screenshot: {
      name: 'screenshot',
      description: 'Take a screenshot of the current screen',
      schema: z.object({}),
      func: async () => {
        const base64 = await computerUse.screenshot();
        return `Screenshot captured (${base64.length} bytes)`;
      },
    },

    navigate: {
      name: 'navigate',
      description: 'Navigate to a URL',
      schema: z.object({
        url: z.string().url().describe('URL to navigate to'),
      }),
      func: async ({ url }: { url: string }) => {
        await computerUse.navigate(url);
        return `Navigated to: ${url}`;
      },
    },

    getScreenSize: {
      name: 'get_screen_size',
      description: 'Get current screen dimensions',
      schema: z.object({}),
      func: async () => {
        const size = await computerUse.getScreenSize();
        return `Screen size: ${size.width}x${size.height}`;
      },
    },
  };
}

