/**
 * Visual oracle - uses multimodal LLM to evaluate screenshots
 * Answers: "Does this look right for what the user is trying to do?"
 */

import Anthropic from '@anthropic-ai/sdk';
import OpenAI from 'openai';
import { OracleEvaluation } from '../types/scenario.js';

export interface OracleConfig {
  provider: 'anthropic' | 'openai';
  model?: string;
  apiKey: string;
}

export class VisualOracle {
  private config: OracleConfig;
  private anthropic?: Anthropic;
  private openai?: OpenAI;

  constructor(config: OracleConfig) {
    this.config = config;

    if (config.provider === 'anthropic') {
      this.anthropic = new Anthropic({ apiKey: config.apiKey });
    } else {
      this.openai = new OpenAI({ apiKey: config.apiKey });
    }
  }

  async evaluate(params: {
    screenshot: string; // base64
    scenarioContext: string;
    stepDescription: string;
    expectedOutcome?: string;
  }): Promise<OracleEvaluation> {
    const prompt = this.buildEvaluationPrompt(params);

    if (this.config.provider === 'anthropic') {
      return this.evaluateWithAnthropic(prompt, params.screenshot);
    } else {
      return this.evaluateWithOpenAI(prompt, params.screenshot);
    }
  }

  private buildEvaluationPrompt(params: {
    scenarioContext: string;
    stepDescription: string;
    expectedOutcome?: string;
  }): string {
    return `You are evaluating a test scenario execution. Based on the screenshot, determine if the expected outcome was achieved.

**Scenario Context:**
${params.scenarioContext}

**Current Step:**
${params.stepDescription}

**Expected Outcome:**
${params.expectedOutcome || 'Step should complete successfully'}

**Evaluate the screenshot and answer:**

1. Does the UI show that this step completed successfully?
2. Are there any visual defects (clipping, overlap, misalignment)?
3. Are there any error messages or confusing UI elements?
4. Would this be confusing to the user?
5. Is the expected benefit being delivered?

**Return your evaluation in this JSON format:**
{
  "status": "pass" | "investigate" | "bug",
  "reasoning": "Your detailed analysis of what you see",
  "confidence": 0.0-1.0,
  "issues": ["list of any issues found"]
}

Be honest and thorough. If something looks wrong, say so. If you're unsure, use "investigate" status.`;
  }

  private async evaluateWithAnthropic(
    prompt: string,
    screenshot: string
  ): Promise<OracleEvaluation> {
    const response = await this.anthropic!.messages.create({
      model: this.config.model || 'claude-3-5-sonnet-20241022',
      max_tokens: 1024,
      messages: [
        {
          role: 'user',
          content: [
            {
              type: 'image',
              source: {
                type: 'base64',
                media_type: 'image/png',
                data: screenshot,
              },
            },
            {
              type: 'text',
              text: prompt,
            },
          ],
        },
      ],
    });

    const textContent = response.content.find((c) => c.type === 'text');
    if (!textContent || textContent.type !== 'text') {
      throw new Error('No text response from Claude');
    }

    return this.parseEvaluationResponse(textContent.text);
  }

  private async evaluateWithOpenAI(
    prompt: string,
    screenshot: string
  ): Promise<OracleEvaluation> {
    const response = await this.openai!.chat.completions.create({
      model: this.config.model || 'gpt-4o',
      max_tokens: 1024,
      messages: [
        {
          role: 'user',
          content: [
            {
              type: 'image_url',
              image_url: {
                url: `data:image/png;base64,${screenshot}`,
              },
            },
            {
              type: 'text',
              text: prompt,
            },
          ],
        },
      ],
    });

    const content = response.choices[0]?.message?.content;
    if (!content) {
      throw new Error('No response from OpenAI');
    }

    return this.parseEvaluationResponse(content);
  }

  private parseEvaluationResponse(text: string): OracleEvaluation {
    // Extract JSON from response (might be wrapped in markdown code blocks)
    const jsonMatch = text.match(/\{[\s\S]*\}/);
    if (!jsonMatch) {
      // Fallback if no JSON found
      return {
        status: 'investigate',
        reasoning: text,
        confidence: 0.5,
        issues: ['Could not parse evaluation response'],
      };
    }

    try {
      const parsed = JSON.parse(jsonMatch[0]);
      return {
        status: parsed.status || 'investigate',
        reasoning: parsed.reasoning || text,
        confidence: parsed.confidence || 0.7,
        issues: parsed.issues || [],
      };
    } catch (error) {
      return {
        status: 'investigate',
        reasoning: text,
        confidence: 0.5,
        issues: ['JSON parse error'],
      };
    }
  }
}

