  oracle: OracleConfig;
  computerUse: ComputerUseTools;
  targetUrl: string;
}

export class ScenarioAgent {
  private oracle: VisualOracle;
  private computerUse: ComputerUseTools;
  private targetUrl: string;
  private model: ChatAnthropic | ChatOpenAI;

  constructor(config: ScenarioAgentConfig) {
    this.oracle = new VisualOracle(config.oracle);
    this.computerUse = config.computerUse;
    this.targetUrl = config.targetUrl;

    // Initialize model
    if (config.oracle.provider === 'anthropic') {
      this.model = new ChatAnthropic({
        modelName: config.oracle.model || 'claude-3-5-sonnet-20241022',
        anthropicApiKey: config.oracle.apiKey,
      });
    } else {
      this.model = new ChatOpenAI({
        modelName: config.oracle.model || 'gpt-4o',
        openAIApiKey: config.oracle.apiKey,
      });
    }
  }

  async executeScenario(scenario: Scenario): Promise<ScenarioResult> {
    const startTime = new Date();
    const executedSteps: ExecutedStep[] = [];
    let currentStepIndex = 0;

    try {
      // Navigate to target URL first
      await this.computerUse.navigate(this.targetUrl);
      await new Promise((resolve) => setTimeout(resolve, 2000)); // Wait for page load

      // Build tools for the agent
      const tools = this.buildTools();
      const toolNode = new ToolNode(tools);

      // Create agent
      const modelWithTools = this.model.bindTools(tools);

      // Build scenario context for the agent
      const scenarioPrompt = this.buildScenarioPrompt(scenario);

      // Execute scenario steps
      for (const step of scenario.steps) {
        console.log(`\nExecuting step ${currentStepIndex + 1}: ${step}`);

        const stepResult = await this.executeStep({
          scenario,
          step,
          stepIndex: currentStepIndex,
        });

        executedSteps.push(stepResult);

        // Check if step failed
        if (stepResult.evaluation.status === 'bug') {
          return this.buildResult({
            scenario,
            executedSteps,
            status: 'fail',
            startTime,
          });
        }

        currentStepIndex++;
      }

      // All steps passed
      return this.buildResult({
        scenario,
        executedSteps,
        status: 'pass',
        startTime,
      });
    } catch (error) {
      console.error('Scenario execution error:', error);
      return this.buildResult({
        scenario,
        executedSteps,
        status: 'error',
        startTime,
        error: error as Error,
      });
    }
  }

  private async executeStep(params: {
    scenario: Scenario;
    step: string;
    stepIndex: number;
  }): Promise<ExecutedStep> {
    const actions: Action[] = [];
    let reasoning = '';

    // Take initial screenshot
    const screenshotBefore = await this.computerUse.screenshot();

    // Build prompt for this step
    const stepPrompt = `
**Scenario:** ${params.scenario.story}

**Your Goal:** ${params.step}

**Instructions:**
1. Take a screenshot to see the current state
2. Determine what actions you need to take to complete this step
3. Execute the actions using the available tools (click, type, press, etc.)
4. Take a final screenshot to verify the step completed

You have access to these tools:
- screenshot(): Take a screenshot
- click(x, y): Click at coordinates
- type(text): Type text
- press(key): Press a key (e.g., "Enter")

Think step by step and explain your reasoning.
`;

    // For MVP, we'll execute step manually based on the step description
    // In a full implementation, this would use the LLM to decide actions
    reasoning = await this.executeStepActions(params.step, actions);

    // Take final screenshot
    const screenshotAfter = await this.computerUse.screenshot();

    // Evaluate with oracle
    const evaluation = await this.oracle.evaluate({
      screenshot: screenshotAfter,
      scenarioContext: params.scenario.story,
      stepDescription: params.step,
      expectedOutcome: params.scenario.expectedOutcomes?.[params.stepIndex],
    });

    console.log(`  Evaluation: ${evaluation.status} (${evaluation.confidence})`);
    console.log(`  Reasoning: ${evaluation.reasoning}`);

    return {
      description: params.step,
      actions,
      screenshot: screenshotAfter,
      reasoning,
      evaluation,
    };
  }

  private async executeStepActions(step: string, actions: Action[]): Promise<string> {
    const stepLower = step.toLowerCase();

    // Simple heuristic-based execution for MVP
    // In full implementation, LLM would decide actions

    if (stepLower.includes('navigate')) {
      // Already navigated
      return 'Navigated to target URL';
    }

    if (stepLower.includes('find') && stepLower.includes('input')) {
      // Take screenshot to locate input
      await this.computerUse.screenshot();
      actions.push({
        type: 'screenshot',
        timestamp: new Date(),
        params: {},
      });
      return 'Located input field visually';
    }

    if (stepLower.includes('type')) {
      // Extract text to type
      const match = step.match(/"([^"]+)"/);
      const text = match ? match[1] : 'Review pull requests';

      // Click in center-ish area where input likely is (640, 150 for TodoMVC)
      await this.computerUse.click(640, 150);
      actions.push({
        type: 'click',
        timestamp: new Date(),
        params: { x: 640, y: 150 },
      });

      await this.computerUse.type(text);
      actions.push({
        type: 'type',
        timestamp: new Date(),
        params: { text },
      });

      return `Typed "${text}" into input field`;
    }

    if (stepLower.includes('press enter') || stepLower.includes('add')) {
      await this.computerUse.press('Enter');
      actions.push({
        type: 'press',
        timestamp: new Date(),
        params: { key: 'Enter' },
      });

      // Wait for UI to update
      await new Promise((resolve) => setTimeout(resolve, 500));

      return 'Pressed Enter to submit';
    }

    if (stepLower.includes('verify')) {
      // Just take screenshot for oracle to evaluate
      await this.computerUse.screenshot();
      actions.push({
        type: 'screenshot',
        timestamp: new Date(),
        params: {},
      });
      return 'Captured state for verification';
    }

    return 'Step executed';
  }

  private buildTools() {
    return [
      tool(
        async (input: { x: number; y: number }) => {
          await this.computerUse.click(input.x, input.y);
          return `Clicked at (${input.x}, ${input.y})`;
        },
        {
          name: 'click',
          description: 'Click at screen coordinates',
          schema: {
            type: 'object',
            properties: {
              x: { type: 'number', description: 'X coordinate' },
              y: { type: 'number', description: 'Y coordinate' },
            },
            required: ['x', 'y'],
          },
        }
      ),
      tool(
        async (input: { text: string }) => {
          await this.computerUse.type(input.text);
          return `Typed: "${input.text}"`;
        },
        {
          name: 'type',
          description: 'Type text at current cursor position',
          schema: {
            type: 'object',
            properties: {
              text: { type: 'string', description: 'Text to type' },
            },
            required: ['text'],
          },
        }
      ),
      tool(
        async () => {
          const screenshot = await this.computerUse.screenshot();
          return `Screenshot captured (${screenshot.length} bytes)`;
        },
        {
          name: 'screenshot',
          description: 'Take a screenshot of the current screen',
          schema: {
            type: 'object',
            properties: {},
          },
        }
      ),
    ];
  }

  private buildScenarioPrompt(scenario: Scenario): string {
    return `You are executing a test scenario. You have computer-use tools to interact with a web application.

**Scenario:**
${scenario.story}

**Goal:**
${scenario.goal}

**Expected Benefit:**
${scenario.expectedBenefit}

**Steps to complete:**
${scenario.steps.map((s, i) => `${i + 1}. ${s}`).join('\n')}

Execute each step carefully, take screenshots to verify your progress, and ensure the expected benefit is delivered.`;
  }

  private buildResult(params: {
    scenario: Scenario;
    executedSteps: ExecutedStep[];
    status: 'pass' | 'fail' | 'error';
    startTime: Date;
    error?: Error;
  }): ScenarioResult {
    const endTime = new Date();
    const lastEvaluation =
      params.executedSteps[params.executedSteps.length - 1]?.evaluation;

    return {
      scenarioId: params.scenario.id,
      status: params.status,
      executedSteps: params.executedSteps,
      evaluation: lastEvaluation || {
        status: 'investigate',
        reasoning: params.error?.message || 'No evaluation',
        confidence: 0,
      },
      metadata: {
        startTime: params.startTime,
        endTime,
        duration: endTime.getTime() - params.startTime.getTime(),
        model: this.oracle['config'].model || 'default',
      },
    };
  }
}

