/**
 * Scenario Testing Agent - Main Entry Point
 */

import { PlaywrightComputerUse } from './computer-use/browser.js';
import { ScenarioAgent } from './agent/scenario-agent.js';
import { todoMVCSimpleScenario } from './scenarios/todomvc-simple.js';
import * as fs from 'fs/promises';
import * as path from 'path';

async function main() {
  console.log('🤖 Scenario Testing Agent - MVP\n');
  console.log('Scenario:', todoMVCSimpleScenario.name);
  console.log('Story:', todoMVCSimpleScenario.story);
  console.log('\n---\n');

  // Get API keys from environment
  const apiKey = process.env.ANTHROPIC_API_KEY || process.env.OPENAI_API_KEY;
  if (!apiKey) {
    console.error('Error: No API key found in environment');
    console.error('Set ANTHROPIC_API_KEY or OPENAI_API_KEY');
    process.exit(1);
  }

  const provider = process.env.ANTHROPIC_API_KEY ? 'anthropic' : 'openai';
  const targetUrl = process.env.TARGET_URL || 'http://localhost:8080';

  console.log(`Using ${provider} model`);
  console.log(`Target URL: ${targetUrl}\n`);

  // Initialize computer use
  const computerUse = new PlaywrightComputerUse();
  await computerUse.initialize({ headless: true });

  try {
    // Create agent
    const agent = new ScenarioAgent({
      oracle: {
        provider: provider as 'anthropic' | 'openai',
        apiKey,
      },
      computerUse,
      targetUrl,
    });

    // Execute scenario
    console.log('Starting scenario execution...\n');
    const result = await agent.executeScenario(todoMVCSimpleScenario);

    // Print results
    console.log('\n=== RESULTS ===\n');
    console.log(`Status: ${result.status.toUpperCase()}`);
    console.log(`Duration: ${result.metadata.duration}ms`);
    console.log(`Steps executed: ${result.executedSteps.length}\n`);

    // Print each step
    result.executedSteps.forEach((step, i) => {
      console.log(`Step ${i + 1}: ${step.description}`);
      console.log(`  Actions: ${step.actions.length}`);
      console.log(`  Evaluation: ${step.evaluation.status}`);
      console.log(`  Confidence: ${step.evaluation.confidence}`);
      console.log(`  Reasoning: ${step.evaluation.reasoning.substring(0, 100)}...`);
      if (step.evaluation.issues && step.evaluation.issues.length > 0) {
        console.log(`  Issues: ${step.evaluation.issues.join(', ')}`);
      }
      console.log();
    });

    // Save results
    const resultsDir = path.join(process.cwd(), 'results');
    await fs.mkdir(resultsDir, { recursive: true });

    const timestamp = new Date().toISOString().replace(/[:.]/g, '-');
    const resultFile = path.join(
      resultsDir,
      `scenario-${todoMVCSimpleScenario.id}-${timestamp}.json`
    );

    await fs.writeFile(resultFile, JSON.stringify(result, null, 2));
    console.log(`Results saved to: ${resultFile}`);

    // Save screenshots
    for (let i = 0; i < result.executedSteps.length; i++) {
      const step = result.executedSteps[i];
      if (step.screenshot) {
        const screenshotFile = path.join(
          resultsDir,
          `screenshot-step-${i + 1}-${timestamp}.png`
        );
        await fs.writeFile(
          screenshotFile,
          Buffer.from(step.screenshot, 'base64')
        );
      }
    }

    console.log('\n✅ Scenario execution complete!');
    process.exit(result.status === 'pass' ? 0 : 1);
  } catch (error) {
    console.error('\n❌ Error executing scenario:', error);
    process.exit(1);
  } finally {
    await computerUse.close();
  }
}

main().catch((error) => {
  console.error('Fatal error:', error);
  process.exit(1);
});

